import logging
import pathlib

#-------------------------------------------------------------------------------

def resolve(path):
    """
    Resolves the existing part of a path.
    """
    if path.exists():
        return path.resolve()
    else:
        return resolve(path.parent) / path.name


class Path(pathlib.PosixPath):
    """
    Customization of 'pathlib.PosixPath'.

      - The path is resolved to an absolute path at construction.
      - Adds convenience methods.

    """

    def __new__(class_, *args, **kw_args):
        if len(args) == 1 and len(kw_args) == 0 and isinstance(args[0], Path):
            return args[0]
        else:
            return resolve(pathlib.PosixPath.__new__(class_, *args, **kw_args))


    def with_suffix(self, suffix):
        """
        @todo
          This hopefully will not be needed when the base method is fixed.
        """
        if suffix is None:
            if self.suffix == "":
                return self
            else:
                return self.parent / self.name[: -len(self.suffix)]
        else:
            return super().with_suffix(suffix)


    # FIXME: Rename to 'has_parent'.
    def starts_with(self, prefix):
        """
        True if 'prefix' a parent of this path.

          >>> prefix = Path('/foo/bar')
          >>> Path('/foo/bar/baz/bif').starts_with(prefix)
          True
          >>> Path('/foo/bar').starts_with(prefix)
          True
          >>> Path('/foo/baz/bif').starts_with(prefix)
          False
          >>> Path('/foo/barbaz/bif').starts_with(prefix)
          False

        """
        return any( p == prefix for p in self.parents )

import html
import functools

#-------------------------------------------------------------------------------

def terminate(terminator, strings):
    result = terminator.join(strings)
    if len(result) > 0:
        result += terminator
    return result


def format_tag(name, attrs={}, close=False):
    if len(attrs) == 0:
        attr_str = ""
    else:
        assert not close
        attr_str = " " + " ".join( 
            '{}="{}"'.format(n, html.escape(v, quote=True)) 
            for n, v in attrs.items() )
    return "<" + ("/" if close else "") + name + attr_str + ">"


class Element:

    def __init__(self, tag, *children, **attrs):
        try:
            attrs["class"] = attrs.pop("class_")
        except KeyError:
            pass

        children = tuple( 
            c if isinstance(c, Element) else str(c) for c in children )
        attrs = { str(n): str(v) for n, v in attrs.items() }

        self.__tag = tag
        self.__attrs = attrs
        self.__children = children


    def __repr__(self):
        return "{}({!r}, *{!r}, **{!r})".format(
            self.__class__.__name__, self.__tag, self.__children, self.__attrs)


    def __str__(self):
        return self.format("", "")


    @property
    def children(self):
        return self.__children


    @property
    def attrs(self):
        # FIXME: Return a read-only view.
        return self.__attrs


    def generate(self, terminator="", indent="", depth=0):
        prefix = indent * depth
        yield prefix + format_tag(self.__tag, self.__attrs) + terminator
        for child in self.__children:
            try:
                yield from child.generate(terminator, indent, depth + 1)
            except AttributeError:
                yield prefix + indent + str(child) + terminator
        yield prefix + format_tag(self.__tag, close=True) + terminator


    def format(self, terminator="\n", indent=" ", depth=0):
        return "".join(self.generate(terminator, indent, depth))



#-------------------------------------------------------------------------------

def _make_element(tag):
    def El(*children, **attrs):
        return Element(tag, *children, **attrs)

    El.__name__ = tag
    return El


_elements = tuple(
    _make_element(n)
    for n in (
        "A",
        "BODY",
        "DIV",
        "EM",
        "HEAD",
        "HTML",
        "IMG",
        "LI",
        "LINK",
        "OL",
        "P",
        "PRE",
        "SCRIPT",
        "SPAN",
        "STYLE",
        "TITLE",
        "UL",
        )
    )

globals().update( (e.__name__, e) for e in _elements )

#-------------------------------------------------------------------------------

__all__ = (
    "Element",
    ) + tuple( e.__name__ for e in _elements )

import functools
import importlib
from   importlib.machinery import SourceFileLoader
import logging
import os
import sys
import types

from   apidoc.path import Path

#-------------------------------------------------------------------------------

@functools.total_ordering
class Name:
    """
    The fully-qualified name of a Python object.
    """

    def __init__(self, parts):
        if isinstance(parts, str):
            parts = tuple(parts.split("."))
        else:
            parts = tuple(parts)
        assert len(parts) > 0
        self.__parts = parts


    def __str__(self):
        return ".".join(self.__parts)


    def __repr__(self):
        return "{}({})".format(
            self.__class__.__name__, 
            ", ".join( repr(p) for p in self.__parts ))


    def __eq__(self, other):
        return str(self) == str(other)


    def __le__(self, other):
        return str(self) < str(other)


    def __hash__(self):
        return hash(self.__parts)


    def __len__(self):
        return len(self.__parts)


    def __iter__(self):
        return iter(self.__parts)


    def __getitem__(self, index):
        return self.__parts[index]


    @property
    def base(self):
        return self.__parts[-1]


    @property
    def parent(self):
        if len(self.__parts) == 1:
            raise AttributeError("name '{}' has no parent".format(self))
        return self.__class__(self.__parts[: -1])


    def __add__(self, part):
        return self.__class__(self.__parts + (part, ))



def get_module_name_from_path(path, base_path):
    """
    Constructs the name of a module from its path relative to the import path.
    """
    path = Path(path)
    base_path = Path(base_path)
    parts = path.with_suffix(None).relative_to(base_path).parts
    return Name(parts)


# FIXME: Don't rely on the module in the path.
def import_module_from_filename(path):
    path = Path(path)
    if path.is_dir():
        # FIXME: Is this general?  Is this right?
        path = path / "__init__.py"

    for load_path in sys.path:
        try:
            relative = path.with_suffix("").relative_to(load_path)
        except ValueError:
            pass
        else:
            name = Name(relative.parts)
            module = importlib.import_module(str(name))
            if Path(module.__file__) == path:
                return name, module
            else:
                logging.warning(
                    "module {} imports from {}, not expected {}".format(
                        name, module.__file__, path))
    raise RuntimeError("{} is not in the Python path".format(path))


def is_package(obj):
    return isinstance(obj, types.ModuleType) and obj.__name__ == obj.__package__


def is_package_dir(path):
    """
    Returns true if 'path' is a package directory.
    """
    path = Path(path)
    return path.is_dir() and (path / "__init__.py").exists()


def enumerate_package(path):
    """
    Generates subpackages and modules under a top-level package.
    """
    path = Path(path)
    if not is_package_dir(path):
        raise ValueError("{} is not a package dir".format(path))

    # The import base is the parent of the top-level package.
    base_path = path.parent

    def enumerate(path):
        yield get_module_name_from_path(path, base_path), path / "__init__.py"
        for sub_path in path.iterdir():
            if sub_path.suffix == ".py" and sub_path.stem != "__init__":
                yield get_module_name_from_path(sub_path, base_path), sub_path
            elif is_package_dir(sub_path):
                yield from enumerate(sub_path)

    return enumerate(path)


def get_submodules(package):
    if not is_package(package):
        raise TypeError("not a package")

    fqname = Name(package.__name__)
    package_dir = Path(package.__file__).parent

    for sub_path in package_dir.iterdir():
        name = sub_path.stem
        if sub_path.suffix == ".py" and name != "__init__":
            yield name, load_module(fqname + name, sub_path)


def load_module(name, path):
    logging.info("loading {} from {}".format(name, path))
    module = SourceFileLoader(str(name), str(path)).load_module()
    if len(name) > 1:
        parent_name = Name(name[: -1])
        parent = sys.modules[str(parent_name)]
        setattr(parent, name[-1], module)
    return module


